import React, { useState } from 'react';
import axios from 'axios';
import './FileUpload.css';

const FileUpload = () => {
  const [file, setFile] = useState(null);
  const [clauses, setClauses] = useState([]);
  const [text, setText] = useState('');
  const [question, setQuestion] = useState('');
  const [response, setResponse] = useState('');

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) return alert("Upload a file first!");
    const formData = new FormData();
    formData.append('file', file);

    try {
      const res = await axios.post('http://localhost:5000/upload', formData);
      setClauses(res.data.clauses);
      setText(res.data.text);
      setResponse('');
    } catch (err) {
      alert('File upload failed');
    }
  };

  const handleAsk = async () => {
    if (!question) return alert("Enter a question!");
    try {
      const res = await axios.post('http://localhost:5000/ask', {
        question,
        context: text
      });
      setResponse(res.data.response || 'No response');
    } catch (err) {
      setResponse('Gemini API error. Try again.');
    }
  };

  return (
    <div className="upload-container">
      <h2>ðŸ“„ Ask Your PDF</h2>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload & Extract</button>

      {clauses.length > 0 && (
        <div className="clauses-box">
          <h4>Extracted Clauses:</h4>
          <ul>
            {clauses.map((clause, i) => (
              <li key={i}>{clause}</li>
            ))}
          </ul>
        </div>
      )}

      <div className="ask-section">
        <input
          type="text"
          placeholder="Ask about a clause..."
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
        />
        <button onClick={handleAsk}>Ask</button>
      </div>

      {response && (
        <div className="response-box">
          <strong>ðŸ¤– Gemini Response:</strong>
          <p>{response}</p>
        </div>
      )}
    </div>
  );
};

export default FileUpload;
