// ðŸ“ frontend/src/components/FileUpload.jsx

import React, { useState } from "react";
import axios from "axios";
import "./FileUpload.css";

const FileUpload = () => {
  const [file, setFile] = useState(null);
  const [clauses, setClauses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [query, setQuery] = useState("");
  const [response, setResponse] = useState("");

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) {
      alert("Please select a file.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    setLoading(true);
    setResponse("");
    setClauses([]);

    try {
      const res = await axios.post("http://localhost:8000/analyze", formData);
      setClauses(res.data.clauses);
    } catch (err) {
      console.error(err);
      alert("Error analyzing file.");
    } finally {
      setLoading(false);
    }
  };

  const handleQuery = async () => {
    if (!query.trim()) return;

    setResponse("Thinking...");

    try {
      const res = await axios.post("http://localhost:8000/parse_query", {
        query: query,
      });

      setResponse(res.data.response);
    } catch (err) {
      console.error(err);
      setResponse("Error reaching backend.");
    }
  };

  return (
    <div className="upload-container">
      <h2>ClauseGPT - PDF Clause Extractor</h2>

      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload & Analyze</button>

      {loading && <p>Analyzing...</p>}

      <div className="results">
        <h3>ðŸ“„ Extracted Clauses</h3>
        {clauses.length > 0 ? (
          <ul>
            {clauses.map((clause, index) => (
              <li key={index}>{clause}</li>
            ))}
          </ul>
        ) : (
          <p>No clauses detected yet.</p>
        )}
      </div>

      <div className="gemini-query">
        <h3>ðŸ¤– Gemini Response</h3>
        <input
          type="text"
          placeholder="Ask about a clause..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button onClick={handleQuery}>Ask</button>

        <p className="response">Answer: {response}</p>
      </div>
    </div>
  );
};

export default FileUpload;
