import React, { useState } from "react";
import axios from "axios";
import "./FileUpload.css";

const FileUpload = () => {
  const [file, setFile] = useState(null);
  const [clauses, setClauses] = useState([]);
  const [selectedClause, setSelectedClause] = useState("");
  const [question, setQuestion] = useState("");
  const [geminiAnswer, setGeminiAnswer] = useState("");
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) return;
    const formData = new FormData();
    formData.append("file", file);

    try {
      setLoading(true);
      const res = await axios.post("http://localhost:5000/upload", formData);
      setClauses(res.data.clauses || []);
      setLoading(false);
    } catch (err) {
      console.error("Upload error:", err);
      setLoading(false);
    }
  };

  const handleAskGemini = async () => {
    if (!selectedClause || !question) return;

    try {
      setLoading(true);
      const res = await axios.post("http://localhost:5000/ask", {
        clause: selectedClause,
        question: question,
      });
      setGeminiAnswer(res.data.answer);
      setLoading(false);
    } catch (err) {
      setGeminiAnswer("❌ Gemini API error. Try again.");
      setLoading(false);
    }
  };

  return (
    <div className="upload-container">
      <h2>ClauseGPT - Upload Contract</h2>

      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>

      {loading && <p>Loading...</p>}

      {clauses.length > 0 && (
        <div className="clauses">
          <h3>Extracted Clauses:</h3>
          <ul>
            {clauses.map((clause, index) => (
              <li
                key={index}
                onClick={() => setSelectedClause(clause)}
                className={selectedClause === clause ? "selected" : ""}
              >
                {clause}
              </li>
            ))}
          </ul>
        </div>
      )}

      {selectedClause && (
        <div className="qa-section">
          <h3>Ask a question about the selected clause:</h3>
          <textarea
            placeholder="Enter your question..."
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
          ></textarea>
          <button onClick={handleAskGemini}>Ask</button>
          {geminiAnswer && (
            <div className="answer">
              <h4>Gemini's Answer:</h4>
              <p>{geminiAnswer}</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default FileUpload;
