import React, { useState } from "react";
import axios from "axios";
import "./FileUpload.css";

const FileUpload = () => {
  const [file, setFile] = useState(null);
  const [clauses, setClauses] = useState([]);
  const [question, setQuestion] = useState("");
  const [response, setResponse] = useState("");
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setClauses([]);
    setResponse("");
  };

  const uploadFile = async () => {
    if (!file) return alert("Please select a file.");
    const formData = new FormData();
    formData.append("file", file);

    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/upload", formData);
      setClauses(res.data.clauses || []);
    } catch (err) {
      alert("File upload failed.");
    }
    setLoading(false);
  };

  const askGemini = async () => {
    if (!question.trim()) return alert("Please type a question.");
    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/ask", { question });
      setResponse(res.data.response);
    } catch (err) {
      alert("Gemini response failed.");
    }
    setLoading(false);
  };

  return (
    <div className="container">
      <h1>ðŸ“„ Ask Your PDF</h1>
      <input type="file" onChange={handleFileChange} />
      <button onClick={uploadFile}>Upload & Extract Clauses</button>

      {clauses.length > 0 && (
        <div className="clauses-box">
          <h3>Extracted Clauses</h3>
          <ul>
            {clauses.map((c, i) => <li key={i}>{c}</li>)}
          </ul>
        </div>
      )}

      <div className="qa-section">
        <input
          type="text"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          placeholder="Ask about a clause..."
        />
        <button onClick={askGemini}>Ask</button>
      </div>

      {response && (
        <div className="response-box">
          <strong>Response:</strong>
          <p>{response}</p>
        </div>
      )}

      {loading && <p>Loading...</p>}
    </div>
  );
};

export default FileUpload;
