// frontend/src/components/FileUpload.jsx

import React, { useState } from "react";
import axios from "axios";
import "./FileUpload.css";

const FileUpload = () => {
  const [file, setFile] = useState(null);
  const [response, setResponse] = useState("");
  const [loading, setLoading] = useState(false);
  const [question, setQuestion] = useState("");

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) return alert("Please upload a PDF file first!");
    setLoading(true);
    const formData = new FormData();
    formData.append("file", file);

    try {
      const uploadRes = await axios.post("http://localhost:5000/upload", formData);
      console.log("File uploaded:", uploadRes.data);
    } catch (err) {
      alert("File upload failed");
    }
    setLoading(false);
  };

  const handleAsk = async () => {
    if (!question) return alert("Please enter a question!");
    setLoading(true);
    try {
      const askRes = await axios.post("http://localhost:5000/ask", { question });
      setResponse(askRes.data.answer || "No answer found.");
    } catch (err) {
      setResponse("‚ùå Error fetching response.");
    }
    setLoading(false);
  };

  return (
    <div className="container">
      <h1>üìÑ Ask Your PDF</h1>
      <div className="upload-area">
        <input type="file" accept=".pdf" onChange={handleFileChange} />
        <button onClick={handleUpload}>Upload PDF</button>
      </div>
      <div className="ask-area">
        <input
          type="text"
          placeholder="Ask about a clause..."
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
        />
        <button onClick={handleAsk} disabled={loading}>
          {loading ? "Thinking..." : "Ask"}
        </button>
      </div>
      <div className="response-area">
        <h3>Response:</h3>
        <div className="response-box">{response}</div>
      </div>
    </div>
  );
};

export default FileUpload;
