import React, { useState } from "react";
import axios from "axios";
import "./FileUpload.css";

const FileUpload = () => {
  const [file, setFile] = useState(null);
  const [clauses, setClauses] = useState([]);
  const [query, setQuery] = useState("");
  const [answer, setAnswer] = useState("");
  const [uploading, setUploading] = useState(false);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
    setClauses([]);
    setAnswer("");
  };

  const handleUpload = async () => {
    if (!file) return;
    setUploading(true);
    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await axios.post("http://localhost:8000/analyze", formData);
      setClauses(res.data.clauses);
    } catch (err) {
      console.error("Error uploading file:", err);
    }
    setUploading(false);
  };

  const handleAsk = async () => {
    if (!query) return;

    try {
      const res = await axios.post("http://localhost:8000/parse_query", {
        query,
      });
      setAnswer(res.data.answer);
    } catch (err) {
      console.error("Error getting answer:", err);
      setAnswer("Error reaching backend.");
    }
  };

  return (
    <div className="container">
      <h1>ClauseGPT - PDF Clause Extractor</h1>

      <div className="upload-box">
        <input type="file" onChange={handleFileChange} />
        <button onClick={handleUpload}>
          {uploading ? "Analyzing..." : "Upload & Analyze"}
        </button>
      </div>

      <div className="results">
        <h2>ðŸ“„ Extracted Clauses</h2>
        {clauses.length > 0 ? (
          <ul>
            {clauses.map((clause, index) => (
              <li key={index}>{clause}</li>
            ))}
          </ul>
        ) : (
          <p>No clauses detected yet.</p>
        )}
      </div>

      <div className="ask-box">
        <h2>ðŸ¤– Gemini Response</h2>
        <input
          type="text"
          value={query}
          placeholder="Ask about a clause..."
          onChange={(e) => setQuery(e.target.value)}
        />
        <button onClick={handleAsk}>Ask</button>
        {answer && (
          <div className="answer-box">
            <strong>Answer:</strong> {answer}
          </div>
        )}
      </div>
    </div>
  );
};

export default FileUpload;
