import React, { useState } from "react";
import axios from "axios";
import "./FileUpload.css";

const FileUpload = () => {
  const [file, setFile] = useState(null);
  const [clauses, setClauses] = useState([]);
  const [inputQuery, setInputQuery] = useState("");
  const [geminiAnswer, setGeminiAnswer] = useState("");
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setClauses([]);
    setGeminiAnswer("");
  };

  const handleUpload = async () => {
    if (!file) {
      alert("Please select a file");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    setLoading(true);
    try {
      const response = await axios.post("http://127.0.0.1:8000/analyze", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setClauses(response.data.clauses);
    } catch (error) {
      console.error("Error uploading file:", error);
    }
    setLoading(false);
  };

  const handleAskQuery = async () => {
    if (!inputQuery.trim()) {
      alert("Please enter a query");
      return;
    }

    try {
      const response = await axios.post("http://127.0.0.1:8000/parse_query", {
        query: inputQuery,
      });
      setGeminiAnswer(response.data.answer || "No answer received.");
    } catch (error) {
      console.error("Error parsing query:", error);
      setGeminiAnswer("Something went wrong.");
    }
  };

  return (
    <div className="upload-container">
      <h2>üìÑ ClauseGPT - Intelligent PDF Analyzer</h2>

      <input type="file" accept=".pdf" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload PDF</button>

      {loading && <p>‚è≥ Extracting clauses...</p>}

      {clauses.length > 0 && (
        <div className="clauses-section">
          <h3>üìå Extracted Clauses:</h3>
          <ul>
            {clauses.map((clause, index) => (
              <li key={index}>{clause}</li>
            ))}
          </ul>
        </div>
      )}

      <div className="query-section">
        <h3>üí¨ Ask a Query</h3>
        <input
          type="text"
          value={inputQuery}
          onChange={(e) => setInputQuery(e.target.value)}
          placeholder="e.g., What is the termination clause?"
        />
        <button onClick={handleAskQuery}>Ask Gemini</button>
        {geminiAnswer && (
          <div className="answer-box">
            <h4>ü§ñ Gemini LLM Response:</h4>
            <p>{geminiAnswer}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default FileUpload;
